import React, { useState } from 'react';
// import { Link, NavLink, BrowserRouter, Route, Routes } from 'react-router-dom';
import axios from 'axios';

function Sign_up() {

    const [Email, setEmail] = useState("");
    const [Name, setName] = useState("");
    const [Password, setPassword] = useState("");

    const onEmailHandler = (event) => {
        setEmail(event.currentTarget.value);
    }

    const onNameHandler = (event) => {
        setName(event.currentTarget.value);
    }

    const onPasswordHandler = (event) => {
        setPassword(event.currentTarget.value);
    }

    const onSubmitHandler = async (event) => {
        event.preventDefault();

        const userData = {
            email: Email,
            name: Name,
            password: Password,
        };

        try {
            //const response = await axios.post('http://localhost:8000/user-service/users', userData);
            const response = await axios.post('http://223.130.135.8:8000/user-service/users', userData);
            console.log(response);

            setEmail('');
            setName('');
            setPassword('');
        } catch (error) {
            console.error('Error submitting data to the REST API:', error);
        }
    }    
    return (
        <div style={{ 
            display: 'flex', justifyContent: 'center', alignItems: 'center', 
            width: '100%', height: '100vh'
            }}>
            <form style={{ display: 'flex', flexDirection: 'column'}}
                onSubmit={onSubmitHandler}            
            >

                <h1>회원가입</h1>            
                <p>이곳은 회원가입 페이지 입니다.</p>            

                <label>Email</label>
                    <input type='email' value={Email} onChange={onEmailHandler}/>

                <label>Name</label>
                    <input type='text' value={Name} onChange={onNameHandler}/>

                <label>Password</label>
                    <input type='password' value={Password} onChange={onPasswordHandler}/>

                <button type="submit">회원가입</button>

            </form>

        </div>
    );
}
export default Sign_up;
